show_version()&#123;
    echo &quot;Prey $&#123;version&#125;&quot;
&#125;
show_usage()&#123;
    echo &quot;Prey $&#123;version&#125; &quot;
    echo -e &quot;\nUsage: `basename $0` [options]&quot;
    echo -e &quot;Options:&quot;
    echo -e &quot;  -v | --version\tShow Current Version.&quot;
    echo -e &quot;  -h | --help\t\tShow this message.\n&quot;
